package Ch1_re;

public class react10 {

	public static void main11(String[] args) {
		// TODO Auto-generated method stub

		char cTemp = 'A';  // char는 한 단어만을 표현할 수 있다. A를 cTemp에 넣어주고 cTemp 출력시 A가 나온다 
		System.out.println(cTemp);
		
		cTemp = 'B';  // cTemp를 이미 인수로 지정해줬기 때문에 앞에 char는 필요 없다.  
		//B를 cTemp에 넣어주고 cTemp 출력시 B가 나온다 
		System.out.println(cTemp);
		
		cTemp = 'C'; 
		System.out.println(cTemp);
		
		
		
		
	}

}
