package Ch1_re;

public class react9 {

	public static void main456(String[] args) { 
		// TODO Auto-generated method stub


		
		byte btemp = 121;    //byte는 -128부터 127까지 출력가능 
		short stemp = 12312;   //short는 -32,768부터 32,767까지 출력가능
		int itemp = - 3456;   // int는 -2,147,483,648부터 2,147,483,647까지 출력 가능 
		long ltemp = 123123223;  // long은 -9,223,372,036,854,775,808부터 9,223,372,036,854,775,807까지 출력 가능 
		float ftemp = 1.4525343f;  // float은 32비트를 가지고 실수를 표현, 소수점뒤에 f를 붙여  소수임을 알려야 함 
		double dtemp = 1.123124124;  //64비트를 가지고 실수를 표현 float과 다르게 뒤에 f를 안붙여도 소수점 표현 가능 
		boolean booltemp = true;   // boolean은 true false만 가능 
		char ctemp = 'A';  // char은 한글자만 가능 
			
		
		System.out.println(btemp);   
		System.out.println(stemp);
		System.out.println(itemp);
		System.out.println(ltemp);
		System.out.println(ftemp);
		System.out.println(dtemp);
		System.out.println(booltemp);
		System.out.println(ctemp);
	
		
	}

}
