package variable;

public class V1 {

	public static void main45(String[] args) {
		// TODO Auto-generated method stub

		
		
		int kopo05_year = 0;  // kopo05_year라는 숫자형 변수 정의 후 초기화  
		int kopo05_age = 0;   // kopo05_age라는 숫자형 변수 정의 후 초기화 
		
		System.out.println("결과1 :" + kopo05_year);  // kopo05_year 출력
		System.out.println("결과2 :" + kopo05_age);  // kopo05_age 출력 
		
		kopo05_year = kopo05_age + 2000;   // kopo05_age에 2000 더해서 kopo05_year에 저장 
		kopo05_age = kopo05_age +1;       // kopo05_age에 1 더해서 kopo05_age에 저장 
		
		System.out.println("결과3 :" + kopo05_year);  // 위에서 2000 더해준 값 kopo05_year 출력 
		System.out.println("결과4 :" + kopo05_age);  // 위에서 1 더해준 값 kopo05_age로 출력 
	
	
	
	}

}
