package variable;

public class V11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		float kopo05_f = 9.1234567f; //  kopo05_f를 소수로 입력  
		double kopo05_d = 9.1234567; // kopo05_d를 소수로 입력 
		double kopo05_d2 = (double)kopo05_f;  // kopo05_f double형식으로 kopo05_d2에 입력  
		
		System.out.printf("kopo05_f = %20.18f\n", kopo05_f);  // kopo05_f 출력 
		System.out.printf("kopo05_d = %20.18f\n", kopo05_d);   //kopo05_d  출력 
		System.out.printf("kopo05_d2 = %20.18f\n", kopo05_d2);  // kopo05_d2 출력 
		
		
		
		
	}

}
