package variable;

public class V3 {

	public static void main45(String[] args) {
		// TODO Auto-generated method stub

		
		String kopo05_name = "JA" + "VA";  // String열로 JA와 VA 연결 
		String kopo05_str = kopo05_name + "8.0";  // kopo05_name은 string열이고 8.0을 ""로 묶어줬기때문에 연결 가능 
		
		System.out.println(kopo05_name); // kopo05_name String열 출력 
		System.out.println(kopo05_str);  // kopo05_str String열 출력 
		System.out.println(7 + " ");    // 7 뒤에 공백 추가해서 출력 
		System.out.println(" " + 7);   // 공백 뒤에 7 추가해서 출력 
		System.out.println(7 + "");   //  ""로 7 String으로 출력  
		System.out.println("" + 7);   // ""로 7 String으로 출력  
		System.out.println("" + "");   // 암것도 출력하지 않음  
		System.out.println(7 + 7 + "");  // 7 두개를 먼저 더해준 다음에 ""으로 string 출력 
		System.out.println("" + 7 + 7);  // 7 두개를 ""로 묶어서 77로 출력 
		
		
		
	}

}
