package variable;

public class V4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println('\''); // ' 출력 
		System.out.println("abc\t123\b456");  // \t로 탭을 하고 \b로 앞에 글자를 지워준다 
		System.out.println('\n');  // \n은 줄 띄우기 
		System.out.println("\"Hello\""); // \"로 앞뒤로 "" 출력  
		System.out.println("c:\\");  // \\을 사용하면 \한개 출력 
		
		
		
		
	}

}
