package variable;

public class V9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		double kopo05_d = 85.4;  // 85.4를 소수형태로 
		int kopo05_score = (int)kopo05_d;  // kopo05_d를 int로 하면 소수점은 버려지는데 그걸 kopo05_score에 넣음
		
		System.out.println("score=" + kopo05_score);  // kopo05_score를 출력하면 int 정수 형태로 
		System.out.println("d=" + kopo05_d);  // kopo05_d를 출력하면 double 소수점 형태로 
		
		
		
		
		
	}

}
